SELECT s.schedule_id, s.name, c.schedule_category_id, c.category_name, c.color, s.is_group, TO_CHAR(s.start_date, 'YYYY-MM-DD HH24:MI') AS startDate, TO_CHAR(s.end_date, 'YYYY-MM-DD HH24:MI') AS endDate, p.place_name FROM member_schedule ms JOIN schedule s ON ms.schedule_id = s.schedule_id JOIN schedule_category c ON s.schedule_category_id = c.schedule_category_id LEFT JOIN schedule_place p ON ms.schedule_id = p.schedule_id WHERE ms.email = #{email}
AND s.is_group = #{isGroup}
AND TO_CHAR(s.start_date, 'YYYY') = #{year} AND TO_CHAR(s.start_date, 'MM') = #{month} AND ((s.is_group = 1 AND p.schedule_id IS NOT NULL) OR s.is_group = 0)

